jQuery(document).ready(function ($) {
    $(".in-accordion__title").click(function () {
      const isActive = $(this).hasClass("active");
      const accordionContent = $(this).siblings(".in-accordion__content");
      const accordionWrap = $(this).parents(".in-accordion-wrap");

      if (isActive) {
        $(this).removeClass("active");
        accordionContent.hide("slow");
      } else {
        accordionWrap.find(".in-accordion__content").hide("slow");
        accordionWrap.find(".in-accordion__title").removeClass("active");
        accordionContent.show("slow");
        $(this).addClass("active");
      }
    });
  });